import { FC, useContext, useEffect } from "react";
import { Card, Row, Col, Button } from "react-bootstrap";
import { useItems } from "../../../hooks/useItems";
import { UsersContext } from "../../../contexts";
import { Link } from "react-router-dom";

type Prop = {
    media_type: string | void
}
const UserCards: FC<Prop> = ({ media_type }) => {
    const { deleteItem, displayItemsFB, itemsFB } = useItems()
    const { currentUser } = useContext(UsersContext)
    

    useEffect(() => {
        displayItemsFB()
    }, [])

    return (

        <div>
            <Row xs={1} md={5} className="g-6">

                {itemsFB?.map((item) => {

                    if (item.media_type) {
                        media_type="tv"
                        return (
                            <Col key={item.idDB}>
                                <Card>
                                    <Card.Img variant="top" src={`http://image.tmdb.org/t/p/w500${item.poster_path}`} />
                                    <Card.Body>
                                        {item.media_type === 'tv' && (
                                            <Card.Title>{item.name}</Card.Title>
                                        )}
                                        <Card.Text> {item.vote_average} </Card.Text>
                                    </Card.Body>
                                    <Card.Footer>
                                        {currentUser?.role === 'admin' && (
                                            <Button variant="danger" onClick={() => { deleteItem(item.id) }}>Eliminar</Button>

                                        )}
                                        {currentUser?.role !== 'admin' && (

                                            <Link to={`/detail/${item.idDB}`} className="warning">Detalle</Link>

                                        )}

                                    </Card.Footer>
                                </Card>
                            </Col>
                        )
                    }
                    else if (item.media_type !== "tv"){     
                        media_type= "movie"                   
                        return (
                            <Col key={item.idDB}>
                                <Card>
                                    <Card.Img variant="top" src={`http://image.tmdb.org/t/p/w500${item.poster_path}`} />
                                    <Card.Body>
                                        <Card.Title>{item.title}</Card.Title>

                                        <Card.Text> {item.vote_average} </Card.Text>
                                    </Card.Body>
                                    <Card.Footer>
                                        {currentUser?.role === 'admin' && (
                                            <Button variant="danger" onClick={() => { deleteItem(item.id) }}>Eliminar</Button>

                                        )}
                                        {currentUser?.role !== 'admin' && (

                                            <Link to={`/detail/${item.idDB}`} className="warning">Detalle</Link>

                                        )}

                                    </Card.Footer>
                                </Card>
                            </Col>

                        )
                    }
                    return ""

                })}
            </Row>
        </div>

    )
}

export { UserCards }
