import { createContext, FC, useState } from "react";
import { User } from "../../types/models";


type Context ={
    users?: User[],
    currentUser?: User,
    updateUsers?: (users:User[]) =>void
    userLogged?:(currentUser: User) =>void
}

const UsersContext = createContext<Context>({
    users: undefined,
    currentUser:undefined,
    updateUsers: () => {},
    userLogged: () => {},
})

const UsersProvider:FC = ({children}) =>{

    const [users, setUsers] = useState<User[]>();
    const [currentUser, setCurrentUser] = useState<User>()

    const updateUsers = (users: User[]) =>{
        setUsers(users)
    }
    const userLogged = (currentUser: User) =>{
        setCurrentUser(currentUser)
    }


    return(
        <UsersContext.Provider value={{users, updateUsers, currentUser, userLogged}}>
            {children}
        </UsersContext.Provider>


    )
}

export {UsersContext, UsersProvider}
