import { FC, useEffect, useState } from "react"
import { Button, Card, Container, Row, Col } from "react-bootstrap"
import { useHistory, useParams } from "react-router-dom"
import { mapToArray } from "../../../helpers"
import { useItems } from "../../../hooks/useItems"
import { Items } from "../../../types/models"
import {itemsApi} from "../../../api/moviesDB"

type ParamsType= {
    id: string
}

const DetailCard: FC = () => {
    const {getItem } = itemsApi
    const [item, setItem] = useState<Items>()
    const { id } = useParams<ParamsType>()
    const {goBack} = useHistory()

    useEffect(() => {
        getItem(id).then((response) => { setItem(response)})
    }, [])


    return (
        <Container>
            <Button variant= "danger" onClick={goBack}> Atrás </Button>
                <Card>
                <Row>
                    <Col md="8">
                    <Card.Body>
                        <Card.Title>{item?.title}</Card.Title>
                        <Card.Text>Descripción: {item?.overview}</Card.Text>
                        <Card.Text>Fecha de estreno: {item?.release_date}</Card.Text>
                    </Card.Body>
                    </Col>
                    <Col md="3">
                    <Card.Img variant="top" src={`http://image.tmdb.org/t/p/w500${item?.poster_path}`} />
                    </Col>

                </Row>
                </Card>

        </Container>
    )
}

export { DetailCard }
