import { api_movies } from "../utils";
import { ApiResponse } from "../types/models";

const getItemsTMDB = async(search: string):Promise <ApiResponse> =>{
    let response
    try{

        if(search){
            response = await api_movies.get<ApiResponse>(`/search/multi?query=${search}`)
        }
        else{
            response = await api_movies.get<ApiResponse>(`/movie/top_rated?`)
        }
        return response.data
    }
    
    catch(err){
        throw new Error()
    }
    
    
    
    
}


export const itemsApi = {getItemsTMDB}
